import React from 'react';
import { connect } from 'react-redux'
import * as ActionTypes from '../store/actions/Tweet'
import { Card, Button, Typography, Paper, Grid, IconButton, CardContent, BottomNavigation } from '@material-ui/core';
import DeleteIcon from '@material-ui/icons/Delete';
import FavouriteSelectedIcon from '@material-ui/icons/Grade'
import FavouriteIcon from '@material-ui/icons/GradeOutlined'


const ShowTweets = (props) => {

    // React Hooks for Editing the Text and updating the values
    const [newText, setNewText] = React.useState('');

    const handleEdit = (item, check) => {

        // set default text in EditBox
        if (check === 'edit') {
            setNewText(item.value);
        }

        const updateValue = { id: item.id, value: newText }

        // onClick of Ok set value in redux
        if (check === 'ok') {
            props.handleUpdate(updateValue);
        }
    }

    if (!props.comments) {
        return null;
    }

    return (
        <React.Fragment>
            <Card>
                {props.comments.map((item) => {
                    return (
                        <Paper style={{borderBottom:'1px solid black'}}>
                            <Grid container xs={12} key={item.id}>
                                <Grid item xs={10}>
                                    <Typography variant="h6" gutterBottom> {item.FriendName}</Typography>
                                </Grid>
                                <Grid xs={1}>
                                    <Button size="small" variant="outlined" >
                                        <DeleteIcon />
                                    </Button>
                                </Grid>
                                <Grid item xs={1}>
                                    <Button size="small" variant="outlined" onClick={() => handleEdit(item, 'edit')}>
                                        <FavouriteIcon />
                                    </Button>
                                </Grid>
                            </Grid>
                        </Paper>
                    )
                })}
            </Card>
        </React.Fragment>
    )
}

const mapStateToProps = (state) => {
    return {
        comments: state.commentMessages
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        handleDelete: (id) => dispatch({ type: ActionTypes.DELETE_TWEET, id }),
        handleUpdate: (item) => dispatch({ type: ActionTypes.EDIT_TWEET, item })
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(ShowTweets);
