import React from 'react';
import { connect } from 'react-redux';
import * as ActionTypes from '../store/actions/FriendListActions';
import { Card, TextField } from '@material-ui/core';

let id = 10;

const TweetUI = (props) => {

    //  React Hook useState instead of state
    const [commentMessage, setCommentMessage] = React.useState('');

    const handleChange = (event) => {
        const value= event.target.value;
        // set data Using Hook
        setCommentMessage(value);
        console.log(event, event.key);
        if ((event.keyCode === 13 || event.key == 'Enter')) {
            console.log('enter pressed')
            handlePostComment(value);
        }
    }

    const handlePostComment = (value) => {

        let listComments = [];
        listComments = [...props.friendsList, { id: id++, friendName: value, favourite: false }];
        props.createTweet(listComments);
        // After submit Clear textarea
        setCommentMessage('');
    }

    return (
        <Card className="card">
            <TextField
                id="standard-name"
                placeholder="Type the name of a friend"
                value={commentMessage}
                onChange={handleChange}
                margin="normal"
                fullWidth
                onKeyPress={handleChange}
                style={{ borderBottom: '1px solid gray' }}
            />
            {/* <Button variant="outlined" color='secondary' type="submit" onClick={handlePostComment}>Post a tweet</Button> */}
        </Card>
    )
}

const mapStateToProps = (state) => {
    const friendsList = state.friendsList
    return { friendsList }
}

const mapDispatchToProps = (dispatch) => {
    return {
        createTweet: (value) => dispatch({ type: ActionTypes.ADD_FRIEND, value })
    }
};

export default connect(mapStateToProps, mapDispatchToProps)(TweetUI);
