import React from 'react';
import { connect } from 'react-redux'
import * as ActionTypes from '../store/actions/FriendListActions'
import { Card, Button, Typography, Paper, Grid, IconButton, CardContent, BottomNavigation } from '@material-ui/core';
import DeleteIcon from '@material-ui/icons/Delete';
import FavouriteSelectedIcon from '@material-ui/icons/Grade'
import FavouriteIcon from '@material-ui/icons/GradeOutlined'


const ShowFriends = (props) => {
    // React Hooks for Editing the Text and updating the values
    const [newText, setNewText] = React.useState('');

    const handleFavouriteUpdate = (item) => {
        console.log(item);
//        const updateValue = { id: item.id, value: newText }
        //props.handleUpdate(updateValue);
    }

    if (!props.friendsList) {
        return null;
    }

    return (
        <React.Fragment>
            <Card className="card">
                {props.friendsList.map((item) => {
                    return (
                        <Grid container key={item.id} className="grid-container">
                            <Grid item xs={8}>
                                <Typography variant="h6" component="h6"> {item.friendName}</Typography>
                                <Typography variant="body2">xxx friends in common</Typography>
                            </Grid>
                            <Grid item xs={2}>
                                <Button variant="outlined" onClick={() => props.handleDelete(item.id)} >
                                    <DeleteIcon />
                                </Button>
                            </Grid>
                            <Grid item xs={2}>
                                <Button variant="outlined" onClick={() => handleFavouriteUpdate(item)}>
                                    {item.favourite ?
                                        <FavouriteSelectedIcon /> :
                                        <FavouriteIcon />}
                                </Button>
                            </Grid>
                        </Grid>
                    )
                })}
            </Card>
        </React.Fragment>
    )
}

const mapStateToProps = (state) => {
    return {
        friendsList: state.friendsList
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        handleDelete: (id) => dispatch({ type: ActionTypes.DELETE_FRIEND, id }),
        handleUpdate: (item) => dispatch({ type: ActionTypes.CHANGE_FAVOURITE_LIST, item })
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(ShowFriends);
