import React from 'react';
import Radio from '@material-ui/core/Radio';
import RadioGroup from '@material-ui/core/RadioGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import FormControl from '@material-ui/core/FormControl';
import FormLabel from '@material-ui/core/FormLabel';

export default  handleGender= (props) => {
  const [value, setValue] = React.useState(props.gender);

  function handleChange(event) {
    setValue(event.target.value);
  }

  return (
    <div >
      <FormControl component="fieldset" >
        <FormLabel component="legend">Gender</FormLabel>
        <RadioGroup
          aria-label="Gender"
          name="gender1"
          value={value}
          onChange={handleChange}
          row
        >
          <FormControlLabel labelPlacement="start" value="female" control={<Radio color="primary"/>} label="Female" />
          <FormControlLabel labelPlacement="start"value="male" control={<Radio color="primary" />} label="Male" />
          <FormControlLabel labelPlacement="start" value="other" control={<Radio color="primary" />} label="Other" />
        </RadioGroup>
      </FormControl>
      </div>
  );
}
