import * as ActionTypes from '../actions/FriendListActions';

const InitialState = {
    friendsList: [{id:1, friendName:'Theodore Roosevelt', favourite: true},{id:2, friendName:'Abraham Lincoln', favourite: false},{id:3, friendName:'Goerge Washington', favourite: false}]
}

const FriendsListReducer = (state = InitialState, action) => {
    let newState;
    switch (action.type) {
        case ActionTypes.ADD_FRIEND:
            newState = {
                ...state,
                friendsList: action.value,
            };
            return newState;
        case ActionTypes.DELETE_FRIEND:
            console.log(action.id)
            const deleteFriend = state.friendsList.filter((item) => item.id !== action.id)
            newState = {
                ...state,
                friendsList: deleteFriend,
            };
            return newState;

        case ActionTypes.CHANGE_FAVOURITE_LIST:
            const editedValue = state.friendsList.map(item => {
                if (item.id === action.item.id) {
                    item.favourite = action.item.favourite;
                    return item;
                }
                else
                    return item;
            });
            newState = {
                ...state,
                friendsList: editedValue
            }
            return newState;
        default:
            return state;
    }

}
export default FriendsListReducer;
